mapper.select:
SELECT SECT_ID, KEY_ID, SEQ, VALUE 
FROM RC_CONFIG
WHERE SECT_ID = #{sectId}

	AND KEY_ID = #{keyId}

ORDER BY SEQ

mapper.insert:
	    INSERT INTO RC_CONFIG
	    (
	    	SECT_ID, KEY_ID, SEQ ,VALUE
    	)
	    VALUES
	    (
		    #{sectId}, #{keyId}, #{seq}, #{value}
	    )
